import React, { useState } from "react";
import { Grid } from "@mui/material";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";
import Snackbar from "@mui/material/Snackbar";
import IconButton from "@mui/material/IconButton";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";

const ReachOutArea = ({
	width,
	label,
	name,
	isMultiline = false,
	value,
	onChange,
	error,
	helperText,
}) => {
	return (
		<Box
			component="div"
			sx={{
				"& > :not(style)": { m: 2, width: width },
			}}
			noValidate
			autoComplete="off"
		>
			<TextField
				required
				id="outlined-basic"
				label={label}
				variant="outlined"
				name={name}
				value={value}
				onChange={onChange}
				{...(isMultiline && { multiline: true })}
				error={error} // Display error if validation fails
				helperText={helperText} // Show the error message
			/>
		</Box>
	);
};

const Mail = () => {
	const [openSnackbar, setOpenSnackbar] = useState(false);
	const [alertMessage, setAlertMessage] = useState("");

	const [formData, setFormData] = useState({
		name: "",
		email: "",
		message: "",
	});
	const [emailError, setEmailError] = useState(false);

	const handleChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({
			...prevData,
			[name]: value,
		}));

		//  Check if the email contains an '@' symbol
		if (name === "email") {
			setEmailError(!value.includes("@"));
		}
	};

	const handleSubmit = (event) => {
		event.preventDefault();

		if (!emailError) {
			// Process the form if there's no email error
			axios
				.post("/mail/send", formData)
				.then((response) => {
					setAlertMessage("Email sent successfully");
					setOpenSnackbar(true);
				})
				.catch((error) => {
					setAlertMessage("Error sending email");
					setOpenSnackbar(true);
				});
		} else {
			// Optionally, handle the case where the form is submitted with an error
			alert("Mail not sent. Please check the form for errors.");
		}
	};

	// =============================
	const handleClose = (event, reason) => {
		if (reason === "clickaway") {
			return;
		}

		setOpenSnackbar(false);
	};

	const action = (
		<React.Fragment>
			<IconButton
				size="small"
				aria-label="close"
				color="inherit"
				onClick={handleClose}
			>
				<CloseIcon fontSize="small" />
			</IconButton>
		</React.Fragment>
	);

	return (
		<>
			<Grid container marginTop={2} spacing={1} className="animate-slide-up">
				<Grid item xs={10} md={10}>
					<ReachOutArea
						width="40ch"
						label="Your Name"
						name="name"
						value={formData.name}
						onChange={handleChange}
					/>
				</Grid>
				<Grid item xs={10} md={10}>
					<ReachOutArea
						width="40ch"
						label="Your Email"
						name="email"
						value={formData.email}
						onChange={handleChange}
						error={emailError} // Pass the email error state
						helperText={
							emailError ? "Invalid email address. Must contain '@'." : ""
						}
					/>
				</Grid>
				<Grid item xs={10} md={10}>
					<ReachOutArea
						width="80ch"
						label="Your Message"
						name="message"
						isMultiline={true}
						value={formData.message}
						onChange={handleChange}
						helperText={"Type your message here. MULTILINE supported."}
					/>
				</Grid>
				<Grid item margin={2} xs={10} md={10} style={{ textAlign: "right" }}>
					<Button variant="contained" onClick={handleSubmit}>
						Send Message &nbsp; <SendIcon fontSize="small" />
					</Button>
				</Grid>
			</Grid>

			{/* Snackbar component */}
			{openSnackbar && (
				<Snackbar
					open={openSnackbar}
					autoHideDuration={6000}
					onClose={handleClose}
				>
					<Alert
						onClose={handleClose}
						severity="success"
						variant="filled"
						sx={{ width: "100%" }}
					>
						This is a success Alert inside a Snackbar!
					</Alert>
				</Snackbar>
			)}
		</>
	);
};

export default Mail;
