import React, { useState } from "react";
import {
	Card,
	CardActionArea,
	CardContent,
	CardMedia,
	Typography,
	Dialog,
	DialogContent,
} from "@mui/material";

const PhotoCard = ({ image }) => {
	const [open, setOpen] = useState(false);

    

	return (
		<>
			<Card sx={{ maxWidth: 345 }}>
				<CardActionArea onClick={() => setOpen(true)}>
					<CardMedia
						component="img"
						height="300px"
						width="auto"
						image={image.url}
						alt={image.title}
					/>
					<CardContent>
						<Typography gutterBottom variant="h5" component="div">
							{image.title}
						</Typography>
						<Typography variant="body2" color="text.secondary">
							{image.title}
						</Typography>
					</CardContent>
				</CardActionArea>
			</Card>

			<Dialog
				open={open}
				onClose={handleClose}
				maxWidth="xl" // Set a larger max width if needed
				fullWidth // Makes the dialog occupy the full width of the screen
				PaperProps={{
					style: { backgroundColor: "transparent", boxShadow: "none" },
				}} // Removes default dialog styles
			>
				<DialogContent style={{ padding: 0 }}>
					<img
						src={image.url}
						alt={image.title}
						style={{
							width: "100%",
							height: "auto",
							maxHeight: "90vh", // Adjust based on your preference
							display: "block",
							margin: "0 auto",
						}}
					/>
				</DialogContent>
			</Dialog>
		</>
	);
};

export default PhotoCard;
