import React from "react";
import { Grid, Typography } from "@mui/material";
import { Link } from "react-router-dom";

const PhotoCard = ({image}) => {
	return (
		<Grid
			container
			sx={{
				width: "100%",
				height: "400px",
				position: "relative",

				backgroundSize: "cover",
				backgroundPosition: "center",
				overflow: "hidden",
				borderRadius: "10px",
				cursor: "pointer",
				color: "white",
				padding: 2,
				textDecoration: "none",

				backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${image.url})`, // Initial dark shade
				"&:hover": {
					backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(${image.url})`, // Darker shade on hover
				},

				"&:hover .description": {
					opacity: 1,
					transform: "translateY(0)", // Slide up on hover
				},
			}}
		>
			<Grid item xs={12}>
				{/* Title Section */}
				<Typography variant="h3">{image.title}</Typography>
			</Grid>

			<Grid item xs={12} alignItems="flex-end" marginBottom={1}>
					<Typography
						variant="caption"
						key={tag}
						fontWeight="bold"
						sx={{
							"&:not(:last-child)": {
								marginRight: "10px",
							},
						}}
					>
						{image.location}
					</Typography>
				))}
			</Grid>
		</Grid>
	);
};

export default PhotoCard;
